operation.add:
			//Autoseotitle
			if ($this->config->get('autoseotitle_enable')) {
				$this->load->model('extension/module/autoseotitle');
				$this->model_extension_module_autoseotitle->setCategory($category_info);
			}

			if (isset($this->request->get['attribute_filter']) || isset($this->request->get['sort']) || isset($this->request->get['order']) || isset($this->request->get['limit']) || isset($this->request->get['filter']) || isset($this->request->get['manufacturer_id']) || isset($this->request->get['in_stock']) || isset($this->request->get['page'])) {
        	    $this->document->setDescription('');
        	    $this->document->setKeywords('');

        	    $data['description'] = false;
        	    $data['add_description'] = false;
        	}
			//Autoseotitle

			//Autoseotitle
			if ($this->config->get('autoseotitle_enable')) {
				$this->load->model('extension/module/autoseotitle');
				$this->model_extension_module_autoseotitle->setManufacturer($manufacturer_info);
			}

        	if (isset($this->request->get['sort']) || isset($this->request->get['order']) || isset($this->request->get['limit']) || isset($this->request->get['filter']) || isset($this->request->get['in_stock']) || isset($this->request->get['page'])) {
        	    $this->document->setDescription('');
        	    $this->document->setKeywords('');

        	    $data['description'] = false;
        	}
